package org.checkerframework.common.returnsreceiver.framework;

import org.checkerframework.framework.type.AnnotatedTypeMirror;

/**
 * A framework supported by the Returns Receiver Checker. That is, some methods generated by the
 * framework return their receiver.
 */
public interface FrameworkSupport {
    /**
     * @param t the method to check
     * @return {@code true} if the method is generated by framework and returns {@code this}
     */
    boolean returnsThis(AnnotatedTypeMirror.AnnotatedExecutableType t);
}
